                $this->_redirect('*/*/');
            }
        }
        $this->_redirect('*/*/');
    }

    protected function _isAllowed()
    {
        if ($this->getRequest()->getActionName() == 'view') {
            return Mage::getSingleton('admin/session')->isAllowed('sales/order/actions/view');
        }
        return Mage::getSingleton('admin/session')->isAllowed('sales/order');
    }
}